    func makeStorage() -> Storage<Data>? {
        return try? Storage<Data>(diskConfig: DiskConfig(name: cd_appId()), memoryConfig: MemoryConfig(expiry: .never, countLimit: 10, totalCostLimit: 10), transformer: TransformerFactory.forData())
