    func onCache() {
        let start = CACurrentMediaTime()
        let storage = self.makeStorage()
        if let data = try? storage?.object(forKey: "string.urlPath") {
            debugPrint("onCache data:", data)
            let end = CACurrentMediaTime()
            debugPrint("onCache data Time:", String(format: "%.7f", (end - start)*1000))
        }
    }
    
    func cache() {
        guard let data = renmingribao.data(using: String.Encoding.utf8) else {
            return
        }
        debugPrint("Cache data:", data)
        let start = CACurrentMediaTime()
        let storage = self.makeStorage()
        try? storage?.setObject(data, forKey: "string.urlPath")
        let end = CACurrentMediaTime()
        debugPrint("Cache data Time:", String(format: "%.7f", (end - start)*1000))
    }

